import React, { useState, useEffect } from 'react';
import { categoryDB, tagDB } from '../utils/db';
import Badge from './Badge';

function Settings({ dbInitialized = false }) {
  const [activeTab, setActiveTab] = useState('categories');
  const [categories, setCategories] = useState([]);
  const [tags, setTags] = useState([]);
  const [newCategory, setNewCategory] = useState({ id: '', name: '' });
  const [newTag, setNewTag] = useState({ id: '', name: '' });
  const [editingCategory, setEditingCategory] = useState(null);
  const [editingTag, setEditingTag] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  // Load categories and tags
  useEffect(() => {
    const loadData = async () => {
      try {
        if (dbInitialized) {
          // Load categories from IndexedDB
          const categoryData = await categoryDB.getAll();
          setCategories(categoryData);

          // Load tags from IndexedDB
          const tagData = await tagDB.getAll();
          setTags(tagData);
        } else {
          // Fallback to localStorage
          loadFromLocalStorage();
        }
      } catch (error) {
        console.error('Error loading data:', error);
        loadFromLocalStorage();
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, [dbInitialized]);

  // Load from localStorage if needed
  const loadFromLocalStorage = () => {
    // Load categories
    const savedCategories = localStorage.getItem('expense-categories');
    if (savedCategories) {
      setCategories(JSON.parse(savedCategories));
    } else {
      // Default categories
      const defaultCategories = [
        { id: 'food', name: 'Food' },
        { id: 'transportation', name: 'Transportation' },
        { id: 'entertainment', name: 'Entertainment' },
        { id: 'utilities', name: 'Utilities' },
        { id: 'housing', name: 'Housing' },
        { id: 'healthcare', name: 'Healthcare' },
        { id: 'other', name: 'Other' }
      ];
      setCategories(defaultCategories);
      localStorage.setItem('expense-categories', JSON.stringify(defaultCategories));
    }

    // Load tags
    const savedTags = localStorage.getItem('expense-tags');
    if (savedTags) {
      setTags(JSON.parse(savedTags));
    } else {
      // Default tags
      const defaultTags = [
        { id: 'essential', name: 'Essential' },
        { id: 'recurring', name: 'Recurring' },
        { id: 'emergency', name: 'Emergency' },
        { id: 'personal', name: 'Personal' },
        { id: 'work', name: 'Work' }
      ];
      setTags(defaultTags);
      localStorage.setItem('expense-tags', JSON.stringify(defaultTags));
    }
  };

  // Save categories to localStorage (if not using IndexedDB)
  useEffect(() => {
    if (!dbInitialized && categories.length > 0) {
      localStorage.setItem('expense-categories', JSON.stringify(categories));
    }
  }, [categories, dbInitialized]);

  // Save tags to localStorage (if not using IndexedDB)
  useEffect(() => {
    if (!dbInitialized && tags.length > 0) {
      localStorage.setItem('expense-tags', JSON.stringify(tags));
    }
  }, [tags, dbInitialized]);

  // Handle form input changes for categories
  const handleCategoryInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'id') {
      // Ensure ID is lowercase, no spaces, and only alphanumeric characters
      const formattedValue = value.toLowerCase().replace(/[^a-z0-9]/g, '');
      setNewCategory({ ...newCategory, [name]: formattedValue });
    } else {
      setNewCategory({ ...newCategory, [name]: value });
    }
  };

  // Handle form input changes for tags
  const handleTagInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'id') {
      // Ensure ID is lowercase, no spaces, and only alphanumeric characters
      const formattedValue = value.toLowerCase().replace(/[^a-z0-9]/g, '');
      setNewTag({ ...newTag, [name]: formattedValue });
    } else {
      setNewTag({ ...newTag, [name]: value });
    }
  };

  // Add new category
  const handleAddCategory = async (e) => {
    e.preventDefault();

    // Validation
    if (!newCategory.id.trim() || !newCategory.name.trim()) {
      setError('Both ID and Name are required');
      return;
    }

    // Check for duplicate ID
    if (categories.some(cat => cat.id === newCategory.id)) {
      setError('A category with this ID already exists');
      return;
    }

    try {
      if (dbInitialized) {
        await categoryDB.add(newCategory);
        const updatedCategories = await categoryDB.getAll();
        setCategories(updatedCategories);
      } else {
        setCategories([...categories, newCategory]);
      }
      setNewCategory({ id: '', name: '' });
      setError('');
    } catch (error) {
      console.error('Error adding category:', error);
      setError('Failed to add category. Please try again.');
    }
  };

  // Add new tag
  const handleAddTag = async (e) => {
    e.preventDefault();

    // Validation
    if (!newTag.id.trim() || !newTag.name.trim()) {
      setError('Both ID and Name are required');
      return;
    }

    // Check for duplicate ID
    if (tags.some(tag => tag.id === newTag.id)) {
      setError('A tag with this ID already exists');
      return;
    }

    try {
      if (dbInitialized) {
        await tagDB.add(newTag);
        const updatedTags = await tagDB.getAll();
        setTags(updatedTags);
      } else {
        setTags([...tags, newTag]);
      }
      setNewTag({ id: '', name: '' });
      setError('');
    } catch (error) {
      console.error('Error adding tag:', error);
      setError('Failed to add tag. Please try again.');
    }
  };

  // Start editing a category
  const handleEditCategoryStart = (category) => {
    setEditingCategory(category);
    setNewCategory({ id: category.id, name: category.name });
  };

  // Start editing a tag
  const handleEditTagStart = (tag) => {
    setEditingTag(tag);
    setNewTag({ id: tag.id, name: tag.name });
  };

  // Save edited category
  const handleSaveCategory = async () => {
    if (!newCategory.id.trim() || !newCategory.name.trim()) {
      setError('Both ID and Name are required');
      return;
    }

    // Check for duplicate ID (excluding the current category being edited)
    if (categories.some(cat => cat.id === newCategory.id && cat.id !== editingCategory.id)) {
      setError('A category with this ID already exists');
      return;
    }

    try {
      if (dbInitialized) {
        await categoryDB.update(newCategory);
        const updatedCategories = await categoryDB.getAll();
        setCategories(updatedCategories);
      } else {
        setCategories(
          categories.map(cat => 
            cat.id === editingCategory.id ? newCategory : cat
          )
        );
      }

      setEditingCategory(null);
      setNewCategory({ id: '', name: '' });
      setError('');
    } catch (error) {
      console.error('Error updating category:', error);
      setError('Failed to update category. Please try again.');
    }
  };

  // Save edited tag
  const handleSaveTag = async () => {
    if (!newTag.id.trim() || !newTag.name.trim()) {
      setError('Both ID and Name are required');
      return;
    }

    // Check for duplicate ID (excluding the current tag being edited)
    if (tags.some(tag => tag.id === newTag.id && tag.id !== editingTag.id)) {
      setError('A tag with this ID already exists');
      return;
    }

    try {
      if (dbInitialized) {
        await tagDB.update(newTag);
        const updatedTags = await tagDB.getAll();
        setTags(updatedTags);
      } else {
        setTags(
          tags.map(tag => 
            tag.id === editingTag.id ? newTag : tag
          )
        );
      }

      setEditingTag(null);
      setNewTag({ id: '', name: '' });
      setError('');
    } catch (error) {
      console.error('Error updating tag:', error);
      setError('Failed to update tag. Please try again.');
    }
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingCategory(null);
    setEditingTag(null);
    setNewCategory({ id: '', name: '' });
    setNewTag({ id: '', name: '' });
    setError('');
  };

  // Delete category
  const handleDeleteCategory = async (id) => {
    // Ask for confirmation
    if (window.confirm('Are you sure you want to delete this category? This may affect existing expenses.')) {
      try {
        if (dbInitialized) {
          await categoryDB.delete(id);
          const updatedCategories = await categoryDB.getAll();
          setCategories(updatedCategories);
        } else {
          setCategories(categories.filter(cat => cat.id !== id));
        }
      } catch (error) {
        console.error('Error deleting category:', error);
        alert('Failed to delete category. Please try again.');
      }
    }
  };

  // Delete tag
  const handleDeleteTag = async (id) => {
    // Ask for confirmation
    if (window.confirm('Are you sure you want to delete this tag? This will remove it from all expenses.')) {
      try {
        if (dbInitialized) {
          await tagDB.delete(id);
          const updatedTags = await tagDB.getAll();
          setTags(updatedTags);
        } else {
          setTags(tags.filter(tag => tag.id !== id));
        }
      } catch (error) {
        console.error('Error deleting tag:', error);
        alert('Failed to delete tag. Please try again.');
      }
    }
  };

  // Color assignments for tags preview
  const tagColors = [
    'blue', 'green', 'yellow', 'indigo', 'purple', 
    'pink', 'lime', 'rose', 'teal', 'cyan', 'orange'
  ];

  const getTagColor = (index) => {
    return tagColors[index % tagColors.length];
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center py-12">
        <div className="text-indigo-400">
          <svg className="animate-spin h-8 w-8 mr-3" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
        </div>
        <p className="text-indigo-300">Loading...</p>
      </div>
    );
  }

  return (
    <div className="pb-20 mb-16">
      <h2 className="text-xl font-semibold mb-6 text-indigo-300">Settings</h2>

      {/* Storage indicator */}
      <div className="mb-4 text-sm">
        <span className="inline-flex items-center text-gray-400">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v2H7a1 1 0 100 2h2v2a1 1 0 102 0v-2h2a1 1 0 100-2h-2V7z" clipRule="evenodd" />
          </svg>
          Storage: {dbInitialized ? 'IndexedDB' : 'LocalStorage'}
        </span>
      </div>

      {/* Tab Navigation */}
      <div className="flex border-b border-gray-700 mb-6">
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'categories' ? 'text-indigo-400 border-b-2 border-indigo-400' : 'text-gray-400 hover:text-gray-300'}`}
          onClick={() => setActiveTab('categories')}
        >
          Categories
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'tags' ? 'text-indigo-400 border-b-2 border-indigo-400' : 'text-gray-400 hover:text-gray-300'}`}
          onClick={() => setActiveTab('tags')}
        >
          Tags
        </button>
      </div>

      {error && (
        <div className="bg-red-900 text-white p-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Categories Tab */}
      {activeTab === 'categories' && (
        <>
          {/* Form for adding/editing categories */}
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg mb-6">
            <h3 className="text-lg font-medium text-white mb-4">
              {editingCategory ? 'Edit Category' : 'Add New Category'}
            </h3>

            <form onSubmit={e => e.preventDefault()}>
              <div className="mb-4">
                <label htmlFor="category-id" className="block mb-2 font-medium text-gray-300">
                  ID (lowercase, no spaces)
                </label>
                <input
                  type="text"
                  id="category-id"
                  name="id"
                  value={newCategory.id}
                  onChange={handleCategoryInputChange}
                  className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="e.g., groceries"
                  disabled={editingCategory !== null}
                />
              </div>

              <div className="mb-4">
                <label htmlFor="category-name" className="block mb-2 font-medium text-gray-300">
                  Display Name
                </label>
                <input
                  type="text"
                  id="category-name"
                  name="name"
                  value={newCategory.name}
                  onChange={handleCategoryInputChange}
                  className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="e.g., Groceries"
                />
              </div>

              <div className="flex gap-2">
                {editingCategory ? (
                  <>
                    <button
                      onClick={handleSaveCategory}
                      className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                    >
                      Save Changes
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-300"
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <button
                    onClick={handleAddCategory}
                    className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                  >
                    Add Category
                  </button>
                )}
              </div>
            </form>
          </div>

          {/* List of categories */}
          <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
            <h3 className="text-lg font-medium text-white p-4 border-b border-gray-700">
              Categories
            </h3>

            {categories.length === 0 ? (
              <div className="p-4 text-gray-400">No categories defined.</div>
            ) : (
              <ul>
                {categories.map(category => (
                  <li key={category.id} className="border-b border-gray-700 last:border-b-0">
                    <div className="flex justify-between items-center p-4">
                      <div>
                        <div className="text-white font-medium">{category.name}</div>
                        <div className="text-gray-400 text-sm">ID: {category.id}</div>
                      </div>
                      <div className="flex gap-2">
                        <button
                          onClick={() => handleEditCategoryStart(category)}
                          className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors duration-300"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteCategory(category.id)}
                          className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition-colors duration-300"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </>
      )}

      {/* Tags Tab */}
      {activeTab === 'tags' && (
        <>
          {/* Form for adding/editing tags */}
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg mb-6">
            <h3 className="text-lg font-medium text-white mb-4">
              {editingTag ? 'Edit Tag' : 'Add New Tag'}
            </h3>

            <form onSubmit={e => e.preventDefault()}>
              <div className="mb-4">
                <label htmlFor="tag-id" className="block mb-2 font-medium text-gray-300">
                  ID (lowercase, no spaces)
                </label>
                <input
                  type="text"
                  id="tag-id"
                  name="id"
                  value={newTag.id}
                  onChange={handleTagInputChange}
                  className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="e.g., essential"
                  disabled={editingTag !== null}
                />
              </div>

              <div className="mb-4">
                <label htmlFor="tag-name" className="block mb-2 font-medium text-gray-300">
                  Display Name
                </label>
                <input
                  type="text"
                  id="tag-name"
                  name="name"
                  value={newTag.name}
                  onChange={handleTagInputChange}
                  className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="e.g., Essential"
                />
              </div>

              <div className="flex gap-2">
                {editingTag ? (
                  <>
                    <button
                      onClick={handleSaveTag}
                      className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                    >
                      Save Changes
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-300"
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <button
                    onClick={handleAddTag}
                    className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                  >
                    Add Tag
                  </button>
                )}
              </div>
            </form>
          </div>

          {/* List of tags */}
          <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
            <h3 className="text-lg font-medium text-white p-4 border-b border-gray-700">
              Tags
            </h3>

            <div className="p-4 flex flex-wrap gap-2 mb-4 border-b border-gray-700">
              {tags.map((tag, index) => (
                <Badge key={tag.id} color={getTagColor(index)}>
                  {tag.name}
                </Badge>
              ))}
            </div>

            {tags.length === 0 ? (
              <div className="p-4 text-gray-400">No tags defined.</div>
            ) : (
              <ul>
                {tags.map(tag => (
                  <li key={tag.id} className="border-b border-gray-700 last:border-b-0">
                    <div className="flex justify-between items-center p-4">
                      <div>
                        <div className="text-white font-medium">{tag.name}</div>
                        <div className="text-gray-400 text-sm">ID: {tag.id}</div>
                      </div>
                      <div className="flex gap-2">
                        <button
                          onClick={() => handleEditTagStart(tag)}
                          className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors duration-300"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteTag(tag.id)}
                          className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition-colors duration-300"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default Settings;
