import React, { useState } from 'react';
import { Combobox, ComboboxLabel, ComboboxOption } from './Combobox';

function ExpenseForm({ addExpense }) {
  const [formData, setFormData] = useState({
    name: '',
    amount: '',
    category: 'food'
  });

  const categories = [
    { id: 'food', name: 'Food' },
    { id: 'transportation', name: 'Transportation' },
    { id: 'entertainment', name: 'Entertainment' },
    { id: 'utilities', name: 'Utilities' },
    { id: 'housing', name: 'Housing' },
    { id: 'healthcare', name: 'Healthcare' },
    { id: 'other', name: 'Other' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCategoryChange = (e) => {
    setFormData({ ...formData, category: e.target.value.id });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (!formData.name.trim() || !formData.amount || isNaN(formData.amount)) {
      alert('Please enter valid expense details');
      return;
    }

    addExpense({
      name: formData.name.trim(),
      amount: parseFloat(formData.amount),
      category: formData.category
    });

    // Reset form
    setFormData({
      name: '',
      amount: '',
      category: 'food'
    });
  };

  // Find the currently selected category object
  const selectedCategory = categories.find(cat => cat.id === formData.category);

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg mb-6">
      <h2 className="text-xl font-semibold mb-4 text-indigo-300">Add New Expense</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="name" className="block mb-2 font-medium text-gray-300">Expense Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="e.g., Groceries"
            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="amount" className="block mb-2 font-medium text-gray-300">Amount (Rp)</label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            placeholder="0"
            step="1000"
            min="1000"
            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="category" className="block mb-2 font-medium text-gray-300">Category</label>
          <Combobox
            name="category"
            options={categories}
            displayValue={(category) => category?.name}
            defaultValue={selectedCategory}
            onChange={handleCategoryChange}
            aria-label="Category"
          >
            {(category) => (
              <ComboboxOption value={category}>
                <ComboboxLabel>{category.name}</ComboboxLabel>
              </ComboboxOption>
            )}
          </Combobox>
        </div>

        <button 
          type="submit" 
          className="px-6 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-gray-800"
        >
          Add Expense
        </button>
      </form>
    </div>
  );
}

export default ExpenseForm;
