import React from 'react';

function ExpenseSummary({ expenses, total }) {
  // Calculate highest expense
  const highestExpense = expenses.length > 0 
    ? Math.max(...expenses.map(expense => expense.amount))
    : 0;

  // Calculate average expense
  const averageExpense = expenses.length > 0 
    ? total / expenses.length
    : 0;

  // Format number as Indonesian Rupiah
  const formatRupiah = (amount) => {
    return new Intl.NumberFormat('id-ID').format(Math.round(amount));
  };

  return (
    <div className="grid grid-cols-4 gap-4 my-6">
      <div className="bg-gray-800 p-4 rounded-lg shadow-lg text-center">
        <div className="text-sm text-gray-400 mb-1">Total Expenses</div>
        <div className="text-2xl font-semibold text-indigo-400">Rp {formatRupiah(total)}</div>
      </div>

      <div className="bg-gray-800 p-4 rounded-lg shadow-lg text-center">
        <div className="text-sm text-gray-400 mb-1">Highest Expense</div>
        <div className="text-2xl font-semibold text-indigo-400">Rp {formatRupiah(highestExpense)}</div>
      </div>

      <div className="bg-gray-800 p-4 rounded-lg shadow-lg text-center">
        <div className="text-sm text-gray-400 mb-1">Average Expense</div>
        <div className="text-2xl font-semibold text-indigo-400">Rp {formatRupiah(averageExpense)}</div>
      </div>

      <div className="bg-gray-800 p-4 rounded-lg shadow-lg text-center">
        <div className="text-sm text-gray-400 mb-1">Total Entries</div>
        <div className="text-2xl font-semibold text-indigo-400">{expenses.length}</div>
      </div>
    </div>
  );
}

export default ExpenseSummary;
