import React, { useState, useRef, useEffect } from 'react';
import Badge from './Badge';

function TagSelector({ selectedTags = [], availableTags = [], onChange }) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const containerRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (containerRef.current && !containerRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Filter tags based on search term
  const filteredTags = availableTags
    .filter(tag => tag.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .filter(tag => !selectedTags.includes(tag.id));

  // Handle tag selection
  const handleTagSelect = (tagId) => {
    const newSelectedTags = [...selectedTags, tagId];
    onChange(newSelectedTags);
    setSearchTerm('');
  };

  // Handle tag removal
  const handleTagRemove = (tagId) => {
    const newSelectedTags = selectedTags.filter(id => id !== tagId);
    onChange(newSelectedTags);
  };

  // Get tag object by id
  const getTagById = (tagId) => {
    return availableTags.find(tag => tag.id === tagId);
  };

  // Color assignments for tags - distribute evenly across available colors
  const tagColors = [
    'blue', 'green', 'yellow', 'indigo', 'purple', 
    'pink', 'lime', 'rose', 'teal', 'cyan', 'orange'
  ];

  const getTagColor = (index) => {
    return tagColors[index % tagColors.length];
  };

  return (
    <div className="relative" ref={containerRef}>
      {/* Selected Tags Display */}
      <div className="mb-2 flex flex-wrap gap-2">
        {selectedTags.map((tagId, index) => {
          const tag = getTagById(tagId);
          return tag ? (
            <Badge 
              key={tagId} 
              color={getTagColor(index)} 
              removable={true}
              onRemove={() => handleTagRemove(tagId)}
            >
              {tag.name}
            </Badge>
          ) : null;
        })}
      </div>

      {/* Tag Selector Button */}
      <div className="relative">
        <button
          type="button"
          className="w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-gray-300 flex items-center justify-between hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          onClick={() => setIsOpen(!isOpen)}
        >
          <span>{selectedTags.length ? `${selectedTags.length} tags selected` : 'Add tags'}</span>
          <svg className="h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </button>

        {/* Dropdown */}
        {isOpen && (
          <div className="absolute z-10 mt-1 w-full rounded-md bg-gray-800 shadow-lg">
            <div className="p-2">
              <input
                type="text"
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="Search tags..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <ul className="max-h-60 overflow-auto py-1">
              {filteredTags.length === 0 ? (
                <li className="px-3 py-2 text-gray-400 text-sm">No tags found</li>
              ) : (
                filteredTags.map((tag, index) => (
                  <li 
                    key={tag.id}
                    className="px-3 py-2 hover:bg-gray-700 cursor-pointer text-gray-300 text-sm"
                    onClick={() => handleTagSelect(tag.id)}
                  >
                    <Badge color={getTagColor(index)}>
                      {tag.name}
                    </Badge>
                  </li>
                ))
              )}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default TagSelector;
