import React, { useState, useEffect } from 'react';
import ExpenseEditModal from './ExpenseEditModal';
import Badge from './Badge';
import { tagDB } from '../utils/db';

function ExpenseList({ expenses, deleteExpense, updateExpense, dbInitialized = false }) {
  const [selectedExpense, setSelectedExpense] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [tags, setTags] = useState([]);

  // Load all tags
  useEffect(() => {
    const loadTags = async () => {
      try {
        if (dbInitialized) {
          // Load from IndexedDB
          const data = await tagDB.getAll();
          setTags(data);
        } else {
          // Fallback to localStorage
          const savedTags = localStorage.getItem('expense-tags');
          if (savedTags) {
            setTags(JSON.parse(savedTags));
          }
        }
      } catch (error) {
        console.error('Error loading tags:', error);
      }
    };

    loadTags();
  }, [dbInitialized]);

  if (expenses.length === 0) {
    return (
      <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
        <div className="p-6 text-center">
          <p className="text-gray-400">No expenses added yet. Start adding some!</p>
        </div>
      </div>
    );
  }

  // Helper function to capitalize category
  const formatCategory = (category) => {
    return category.charAt(0).toUpperCase() + category.slice(1);
  };

  // Format number as Indonesian Rupiah
  const formatRupiah = (amount) => {
    return new Intl.NumberFormat('id-ID').format(amount);
  };

  const handleExpenseClick = (expense) => {
    setSelectedExpense(expense);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedExpense(null);
  };

  const handleEditExpense = (updatedExpense) => {
    updateExpense(updatedExpense);
    setIsModalOpen(false);
    setSelectedExpense(null);
  };

  // Get tag name by ID
  const getTagNameById = (tagId) => {
    const tag = tags.find(t => t.id === tagId);
    return tag ? tag.name : tagId;
  };

  // Color assignments for tags - distribute evenly across available colors
  const tagColors = [
    'blue', 'green', 'yellow', 'indigo', 'purple', 
    'pink', 'lime', 'rose', 'teal', 'cyan', 'orange'
  ];

  const getTagColor = (index) => {
    return tagColors[index % tagColors.length];
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4 text-indigo-300">Expense History</h2>
      <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
        {expenses.map((expense) => (
          <div 
            key={expense.id} 
            className="p-4 border-b border-gray-700 hover:bg-gray-700 cursor-pointer transition-colors duration-200"
            onClick={() => handleExpenseClick(expense)}
          >
            <div className="flex justify-between items-center mb-2">
              <div className="font-medium text-white">{expense.name}</div>
              <div className="flex items-center">
                <div className="text-gray-400 mr-4">
                  {formatCategory(expense.category)}
                </div>
                <div className="text-red-400 font-medium mr-3">Rp {formatRupiah(expense.amount)}</div>
                <button 
                  className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition-colors duration-300"
                  onClick={(e) => {
                    e.stopPropagation(); // Prevent row click event
                    deleteExpense(expense.id);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>

            {/* Display Tags */}
            {expense.tags && expense.tags.length > 0 && (
              <div className="flex flex-wrap gap-2 mt-2">
                {expense.tags.map((tagId, index) => (
                  <Badge key={`${expense.id}-${tagId}`} color={getTagColor(index)}>
                    {getTagNameById(tagId)}
                  </Badge>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Edit Modal */}
      {isModalOpen && selectedExpense && (
        <ExpenseEditModal
          expense={selectedExpense}
          onSave={handleEditExpense}
          onCancel={handleCloseModal}
          dbInitialized={dbInitialized}
        />
      )}
    </div>
  );
}

export default ExpenseList;
