import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import { Combobox, ComboboxLabel, ComboboxOption } from './Combobox';
import TagSelector from './TagSelector';

function FilterSortModal({
  isOpen,
  onClose,
  onApply,
  initialFilters,
  initialSort,
  availableCategories = [],
  availableTags = [],
  availableWallets = []
}) {
  const [filters, setFilters] = useState(initialFilters);
  const [sort, setSort] = useState(initialSort);

  useEffect(() => {
    setFilters(initialFilters);
    setSort(initialSort);
  }, [initialFilters, initialSort]);

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSortChange = (field, order) => {
    setSort({ field, order });
  };

  const handleApply = () => {
    onApply(filters, sort);
  };

  const handleClearFilters = () => {
    setFilters({
      type: 'all', // 'all', 'income', 'expense'
      walletId: 'all',
      categoryId: 'all',
      tags: [],
      dateRange: 'all', // 'all', 'today', 'thisWeek', 'thisMonth', 'custom'
      startDate: '',
      endDate: '',
    });
    setSort({
      field: 'date', // 'date', 'amount'
      order: 'desc' // 'asc', 'desc'
    });
  };

  // Date range options
  const dateRangeOptions = [
    { id: 'all', name: 'All Time' },
    { id: 'today', name: 'Today' },
    { id: 'thisWeek', name: 'This Week' },
    { id: 'thisMonth', name: 'This Month' },
    // { id: 'custom', name: 'Custom Range' }, // Enable later if needed
  ];

  // Sort field options
  const sortFieldOptions = [
    { id: 'date', name: 'Date' },
    { id: 'amount', name: 'Amount' },
  ];

  // Sort order options
  const sortOrderOptions = [
    { id: 'desc', name: 'Newest / Highest' },
    { id: 'asc', name: 'Oldest / Lowest' },
  ];

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div className="bg-gray-800 rounded-lg shadow-lg p-6 w-full max-w-md flex flex-col h-[80vh]">
        {/* Sticky header */}
        <div className="sticky top-0 left-0 right-0 px-6 py-4 bg-gray-800 border-b border-gray-700 z-10">
          <div className="flex justify-between items-center">
            <h3 className="text-lg font-medium text-white">Filter & Sort</h3>
            <button
              type="button"
              className="text-gray-400 hover:text-white p-2 rounded-full bg-gray-700 bg-opacity-50"
              onClick={onClose}
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        {/* Scrollable content */}
        <div className="flex-1 overflow-y-auto p-6">
          {/* Filter Section */}
          <div className="mb-6">
            <h4 className="text-md font-medium text-white mb-3">Filter by:</h4>

            {/* Type Filter */}
            <div className="mb-4">
              <label className="block text-gray-300 mb-2">Type</label>
              <div className="flex bg-gray-700 rounded-md p-1">
                <button
                  type="button"
                  className={`flex-1 py-2 px-4 rounded ${
                    filters.type === 'all' ? 'bg-indigo-600 text-white' : 'text-gray-300'
                  }`}
                  onClick={() => handleFilterChange('type', 'all')}
                >
                  All
                </button>
                <button
                  type="button"
                  className={`flex-1 py-2 px-4 rounded ${
                    filters.type === 'expense' ? 'bg-indigo-600 text-white' : 'text-gray-300'
                  }`}
                  onClick={() => handleFilterChange('type', 'expense')}
                >
                  Expense
                </button>
                <button
                  type="button"
                  className={`flex-1 py-2 px-4 rounded ${
                    filters.type === 'income' ? 'bg-green-600 text-white' : 'text-gray-300'
                  }`}
                  onClick={() => handleFilterChange('type', 'income')}
                >
                  Income
                </button>
              </div>
            </div>

            {/* Date Range Filter */}
            <div className="mb-4">
              <label htmlFor="dateRange" className="block text-gray-300 mb-2">Date Range</label>
                <Combobox
                  name="dateRange"
                  options={dateRangeOptions}
                  displayValue={(option) => option.name}
                  defaultValue={dateRangeOptions.find(opt => opt.id === filters.dateRange)}
                  onChange={(option) => handleFilterChange('dateRange', option.id)}
                  aria-label="Date Range"
                >
                  {dateRangeOptions.map((option) => (
                    <ComboboxOption key={option.id} value={option}>
                      {option.name}
                    </ComboboxOption>
                  ))}
                </Combobox>
            </div>

            {/* Wallet Filter */}
            <div className="mb-4">
              <label htmlFor="wallet" className="block text-gray-300 mb-2">Wallet</label>
              <Combobox
                name="walletId"
                options={[{ id: 'all', name: 'All Wallets' }, ...availableWallets]}
                displayValue={(option) => option?.name}
                defaultValue={availableWallets.find(w => w.id === filters.walletId) || { id: 'all', name: 'All Wallets' }}
                onChange={(option) => handleFilterChange('walletId', option.id)}
                aria-label="Wallet Filter"
              >
                {[{ id: 'all', name: 'All Wallets' }, ...availableWallets].map((wallet) => (
                  <ComboboxOption key={wallet.id} value={wallet}>
                    {wallet.name}
                  </ComboboxOption>
                ))}
              </Combobox>
            </div>

            {/* Category Filter */}
            <div className="mb-4">
              <label htmlFor="category" className="block text-gray-300 mb-2">Category</label>
              <Combobox
                name="categoryId"
                options={[{ id: 'all', name: 'All Categories' }, ...availableCategories]}
                displayValue={(option) => option?.name}
                defaultValue={availableCategories.find(cat => cat.id === filters.categoryId) || { id: 'all', name: 'All Categories' }}
                onChange={(option) => handleFilterChange('categoryId', option.id)}
                aria-label="Category Filter"
              >
                {[{ id: 'all', name: 'All Categories' }, ...availableCategories].map((category) => (
                  <ComboboxOption key={category.id} value={category}>
                    {category.name}
                  </ComboboxOption>
                ))}
              </Combobox>
            </div>

            {/* Tags Filter */}
            {/* This requires a multi-select tag component, which we might need to build or adapt TagSelector */}
            {/* For now, we'll leave this as a placeholder or simplify */}
             <div className="mb-4">
              <label className="block text-gray-300 mb-2">Tags</label>
               {/* TagSelector needs adaptation for filtering, not just selecting */}
               {/* Leaving as placeholder for now */}
              <p className="text-gray-500 text-sm">Tag filtering coming soon...</p>
             </div>
          </div>

          {/* Sort Section */}
          <div className="mb-6">
            <h4 className="text-md font-medium text-white mb-3">Sort by:</h4>

            {/* Sort Field */}
            <div className="mb-4">
              <label htmlFor="sortField" className="block text-gray-300 mb-2">Field</label>
              <Combobox
                name="sortField"
                options={sortFieldOptions}
                displayValue={(option) => option.name}
                defaultValue={sortFieldOptions.find(opt => opt.id === sort.field)}
                onChange={(option) => handleSortChange(option.id, sort.order)}
                aria-label="Sort Field"
              >
                 {sortFieldOptions.map((option) => (
                    <ComboboxOption key={option.id} value={option}>
                      {option.name}
                    </ComboboxOption>
                  ))}
              </Combobox>
            </div>

            {/* Sort Order */}
             <div className="mb-4">
              <label htmlFor="sortOrder" className="block text-gray-300 mb-2">Order</label>
              <Combobox
                name="sortOrder"
                options={sortOrderOptions}
                displayValue={(option) => option.name}
                defaultValue={sortOrderOptions.find(opt => opt.id === sort.order)}
                onChange={(option) => handleSortChange(sort.field, option.id)}
                aria-label="Sort Order"
              >
                 {sortOrderOptions.map((option) => (
                    <ComboboxOption key={option.id} value={option}>
                      {option.name}
                    </ComboboxOption>
                  ))}
              </Combobox>
            </div>
          </div>
        </div>

        {/* Sticky footer */}
        <div className="sticky bottom-0 left-0 right-0 px-6 py-4 bg-gray-800 border-t border-gray-700 shadow-lg flex justify-between">
          <button
            type="button"
            className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-300"
            onClick={handleClearFilters}
          >
            Clear Filters
          </button>
          <button
            type="button"
            className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
            onClick={handleApply}
          >
            Apply Filters
          </button>
        </div>
      </div>
    </Modal>
  );
}

export default FilterSortModal;
