import React, { useState, useEffect } from 'react';
import { Combobox, ComboboxLabel, ComboboxOption } from './Combobox';
import { categoryDB } from '../utils/db';

function ExpenseForm({ addExpense, dbInitialized = false }) {
  const [formData, setFormData] = useState({
    name: '',
    amount: '',
    category: 'makanan'
  });

  const [categories, setCategories] = useState([
    { id: 'makanan', name: 'Makanan Sehari-hari' },
    { id: 'transportasi_umum', name: 'Transportasi Umum' },
    { id: 'hiburan', name: 'Hiburan & Rekreasi' },
    { id: 'listrik', name: 'Listrik' },
    { id: 'sewa_rumah', name: 'Sewa Rumah/Kos' },
    { id: 'kesehatan', name: 'Kesehatan & Obat' },
    { id: 'lainnya', name: 'Pengeluaran Lainnya' }
  ]);

  // Load categories
  useEffect(() => {
    const loadCategories = async () => {
      try {
        if (dbInitialized) {
          // Load from IndexedDB
          const data = await categoryDB.getAll();
          if (data.length > 0) {
            setCategories(data);

            // Set default category if it exists in the loaded categories
            const defaultCategory = data.find(cat => cat.id === 'makanan') || data[0];
            if (defaultCategory) {
              setFormData(prev => ({ ...prev, category: defaultCategory.id }));
            }
          }
        } else {
          // Fallback to localStorage
          loadFromLocalStorage();
        }
      } catch (error) {
        console.error('Error loading categories:', error);
        loadFromLocalStorage();
      }
    };

    loadCategories();
  }, [dbInitialized]);

  // Load categories from localStorage if needed
  const loadFromLocalStorage = () => {
    const savedCategories = localStorage.getItem('expense-categories');
    if (savedCategories) {
      const parsedCategories = JSON.parse(savedCategories);
      setCategories(parsedCategories);

      // Set default category if it exists in the loaded categories
      const defaultCategory = parsedCategories.find(cat => cat.id === 'makanan') || parsedCategories[0];
      if (defaultCategory) {
        setFormData(prev => ({ ...prev, category: defaultCategory.id }));
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCategoryChange = (e) => {
    setFormData({ ...formData, category: e.target.value.id });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (!formData.name.trim() || !formData.amount || isNaN(formData.amount)) {
      alert('Please enter valid expense details');
      return;
    }

    addExpense({
      name: formData.name.trim(),
      amount: parseFloat(formData.amount),
      category: formData.category
    });

    // Reset form
    setFormData({
      name: '',
      amount: '',
      category: formData.category // Keep the same category for convenience
    });
  };

  // Find the currently selected category object
  const selectedCategory = categories.find(cat => cat.id === formData.category);

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
      <h2 className="text-xl font-semibold mb-4 text-indigo-300">Add New Expense</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="name" className="block mb-2 font-medium text-gray-300">Expense Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="e.g., Groceries"
            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
            autoFocus
          />
        </div>

        <div className="mb-4">
          <label htmlFor="amount" className="block mb-2 font-medium text-gray-300">Amount (Rp)</label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            placeholder="0"
            step="1000"
            min="1000"
            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
        </div>

        <div className="mb-5">
          <label htmlFor="category" className="block mb-2 font-medium text-gray-300">Category</label>
          {categories.length > 0 ? (
            <Combobox
              name="category"
              options={categories}
              displayValue={(category) => category?.name}
              defaultValue={selectedCategory}
              onChange={handleCategoryChange}
              aria-label="Category"
            >
              {(category) => (
                <ComboboxOption value={category}>
                  <ComboboxLabel>{category.name}</ComboboxLabel>
                </ComboboxOption>
              )}
            </Combobox>
          ) : (
            <div className="text-gray-400 py-2">
              No categories available. Please add some in Settings.
            </div>
          )}
        </div>

        <div className="flex justify-end">
          <button 
            type="submit" 
            className="px-6 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-gray-800"
            disabled={categories.length === 0}
          >
            Add Expense
          </button>
        </div>
      </form>
    </div>
  );
}

export default ExpenseForm;
