import React from 'react';

function ExpenseList({ expenses, deleteExpense }) {
  if (expenses.length === 0) {
    return (
      <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
        <div className="p-6 text-center">
          <p className="text-gray-400">No expenses added yet. Start adding some!</p>
        </div>
      </div>
    );
  }

  // Helper function to capitalize category
  const formatCategory = (category) => {
    return category.charAt(0).toUpperCase() + category.slice(1);
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4 text-indigo-300">Expense History</h2>
      <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
        {expenses.map((expense) => (
          <div key={expense.id} className="flex justify-between items-center p-4 border-b border-gray-700">
            <div className="font-medium text-white">{expense.name}</div>
            <div className="flex items-center">
              <div className="text-gray-400 mr-4">
                {formatCategory(expense.category)}
              </div>
              <div className="text-red-400 font-medium mr-3">${expense.amount.toFixed(2)}</div>
              <button 
                className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition-colors duration-300"
                onClick={() => deleteExpense(expense.id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ExpenseList;
