import React from 'react';

const colorClasses = {
  blue: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
  gray: 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-300',
  red: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300',
  green: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
  yellow: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
  indigo: 'bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-300',
  purple: 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300',
  pink: 'bg-pink-100 text-pink-800 dark:bg-pink-900 dark:text-pink-300',
  lime: 'bg-lime-100 text-lime-800 dark:bg-lime-900 dark:text-lime-300',
  rose: 'bg-rose-100 text-rose-800 dark:bg-rose-900 dark:text-rose-300',
  teal: 'bg-teal-100 text-teal-800 dark:bg-teal-900 dark:text-teal-300',
  cyan: 'bg-cyan-100 text-cyan-800 dark:bg-cyan-900 dark:text-cyan-300',
  orange: 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-300',
};

export function Badge({ 
  children, 
  color = 'blue', 
  className = '', 
  removable = false, 
  onRemove,
  onClick
}) {
  const handleClick = (e) => {
    // If it's removable, clicking anywhere on the badge will trigger removal
    if (removable && onRemove) {
      e.stopPropagation();
      onRemove();
    } 
    // Otherwise, if it's a regular clickable badge (for selection)
    else if (onClick) {
      e.stopPropagation();
      onClick(e);
    }
  };

  return (
    <span 
      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${colorClasses[color] || colorClasses.blue} ${className} ${(onClick || removable) ? 'cursor-pointer hover:opacity-80 transition-opacity' : ''}`}
      onClick={handleClick}
    >
      {children}
      {removable && (
        <span className="flex-shrink-0 ml-1 h-4 w-4 rounded-full inline-flex items-center justify-center">
          <svg className="h-2 w-2" stroke="currentColor" fill="none" viewBox="0 0 8 8">
            <path strokeLinecap="round" strokeWidth="1.5" d="M1 1l6 6m0-6L1 7" />
          </svg>
        </span>
      )}
    </span>
  );
}

export default Badge;
