import React, { useState, useEffect } from 'react';
import { Combobox, ComboboxLabel, ComboboxOption } from './Combobox';
import { categoryDB } from '../utils/db';

function ExpenseEditModal({ expense, onSave, onCancel, dbInitialized = false }) {
  const [formData, setFormData] = useState({
    id: expense.id,
    name: expense.name,
    amount: expense.amount.toString(),
    category: expense.category,
    date: expense.date || new Date().toISOString().slice(0, 10)
  });

  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Load categories
  useEffect(() => {
    const loadCategories = async () => {
      try {
        if (dbInitialized) {
          // Load from IndexedDB
          const data = await categoryDB.getAll();
          if (data.length > 0) {
            setCategories(data);
          } else {
            // Fallback to localStorage if no categories in IndexedDB
            loadFromLocalStorage();
          }
        } else {
          // Fallback to localStorage
          loadFromLocalStorage();
        }
      } catch (error) {
        console.error('Error loading categories:', error);
        loadFromLocalStorage();
      } finally {
        setIsLoading(false);
      }
    };

    loadCategories();
  }, [dbInitialized]);

  // Load categories from localStorage if needed
  const loadFromLocalStorage = () => {
    const savedCategories = localStorage.getItem('expense-categories');
    if (savedCategories) {
      setCategories(JSON.parse(savedCategories));
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCategoryChange = (e) => {
    setFormData({ ...formData, category: e.target.value.id });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (!formData.name.trim() || !formData.amount || isNaN(formData.amount)) {
      alert('Please enter valid expense details');
      return;
    }

    // Save changes
    onSave({
      ...formData,
      amount: parseFloat(formData.amount)
    });
  };

  // Find the currently selected category object
  const selectedCategory = categories.find(cat => cat.id === formData.category);

  if (isLoading) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-gray-800 rounded-lg shadow-lg p-6">
          <div className="flex justify-center items-center">
            <div className="text-indigo-400">
              <svg className="animate-spin h-8 w-8 mr-3" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
            </div>
            <p className="text-indigo-300">Loading...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg shadow-lg w-full max-w-md overflow-hidden">
        <div className="p-6">
          <h2 className="text-xl font-semibold mb-4 text-indigo-300">Edit Expense</h2>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="name" className="block mb-2 font-medium text-gray-300">Expense Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="amount" className="block mb-2 font-medium text-gray-300">Amount (Rp)</label>
              <input
                type="number"
                id="amount"
                name="amount"
                value={formData.amount}
                onChange={handleChange}
                placeholder="0"
                step="1000"
                min="1000"
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="category" className="block mb-2 font-medium text-gray-300">Category</label>
              {categories.length > 0 ? (
                <Combobox
                  name="category"
                  options={categories}
                  displayValue={(category) => category?.name}
                  defaultValue={selectedCategory}
                  onChange={handleCategoryChange}
                  aria-label="Category"
                >
                  {(category) => (
                    <ComboboxOption value={category}>
                      <ComboboxLabel>{category.name}</ComboboxLabel>
                    </ComboboxOption>
                  )}
                </Combobox>
              ) : (
                <div className="text-gray-400 py-2">
                  No categories available. Please add some in Settings.
                </div>
              )}
            </div>

            <div className="flex justify-end gap-2 mt-6">
              <button
                type="button"
                onClick={onCancel}
                className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-300"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                disabled={categories.length === 0}
              >
                Save Changes
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ExpenseEditModal;
