import React, { useState } from 'react';
import Badge from './Badge';

function TagSelector({ selectedTags = [], availableTags = [], onChange }) {
  const [searchTerm, setSearchTerm] = useState('');

  // Filter tags based on search term
  const filteredTags = availableTags
    .filter(tag => tag.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .filter(tag => !selectedTags.includes(tag.id));

  // Handle tag selection
  const handleTagSelect = (tagId) => {
    const newSelectedTags = [...selectedTags, tagId];
    onChange(newSelectedTags);
    setSearchTerm('');
  };

  // Handle tag removal
  const handleTagRemove = (tagId) => {
    const newSelectedTags = selectedTags.filter(id => id !== tagId);
    onChange(newSelectedTags);
  };

  // Get tag object by id
  const getTagById = (tagId) => {
    return availableTags.find(tag => tag.id === tagId);
  };

  // Color assignments for tags - distribute evenly across available colors
  const tagColors = [
    'blue', 'green', 'yellow', 'indigo', 'purple', 
    'pink', 'lime', 'rose', 'teal', 'cyan', 'orange'
  ];

  const getTagColor = (index) => {
    return tagColors[index % tagColors.length];
  };

  return (
    <div className="space-y-4">
      {/* Selected Tags Section */}
      {selectedTags.length > 0 && (
        <div className="p-4 flex flex-wrap gap-2 mb-4 border-b border-gray-700">
          {selectedTags.map((tagId, index) => {
            const tag = getTagById(tagId);
            return tag ? (
              <Badge 
                key={tagId} 
                color={getTagColor(availableTags.findIndex(t => t.id === tagId))} 
                removable={true}
                onRemove={() => handleTagRemove(tagId)}
              >
                {tag.name}
              </Badge>
            ) : null;
          })}
        </div>
      )}

      {/* Search Box */}
      <div className="relative mb-4">
        <input
          type="text"
          className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
          placeholder="Search tags..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Available Tags */}
      <div className="flex flex-wrap gap-2">
        {filteredTags.length === 0 ? (
          <p className="text-gray-400 text-sm">No matching tags found</p>
        ) : (
          filteredTags.map((tag, index) => (
            <Badge 
              key={tag.id}
              color={getTagColor(availableTags.findIndex(t => t.id === tag.id))}
              className="cursor-pointer hover:opacity-80 transition-opacity"
              onClick={() => handleTagSelect(tag.id)}
            >
              {tag.name}
            </Badge>
          ))
        )}
      </div>
    </div>
  );
}

export default TagSelector;
