import React, { useState, useEffect } from 'react';
import ExpenseForm from './components/ExpenseForm';
import ExpenseList from './components/ExpenseList';
import ExpenseSummary from './components/ExpenseSummary';
import ExpenseChart from './components/ExpenseChart';
import Settings from './components/Settings';
import BottomNavigation from './components/BottomNavigation';
import { initializeDatabase, expenseDB, categoryDB } from './utils/db';

function App() {
  const [activeTab, setActiveTab] = useState('home');
  const [expenses, setExpenses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [dbInitialized, setDbInitialized] = useState(false);

  // Initialize database
  useEffect(() => {
    const init = async () => {
      try {
        const initialized = await initializeDatabase();
        setDbInitialized(initialized);

        if (initialized) {
          // Load all expenses after DB initialization
          loadExpenses();
        } else {
          // Fall back to localStorage if IndexedDB fails
          fallbackToLocalStorage();
        }
      } catch (error) {
        console.error('Failed to initialize database:', error);
        fallbackToLocalStorage();
      } finally {
        setIsLoading(false);
      }
    };

    init();
  }, []);

  // Fallback to localStorage if IndexedDB fails
  const fallbackToLocalStorage = () => {
    const savedExpenses = localStorage.getItem('expenses');
    if (savedExpenses) {
      setExpenses(JSON.parse(savedExpenses));
    }
    setIsLoading(false);
  };

  // Load expenses from IndexedDB
  const loadExpenses = async () => {
    try {
      const allExpenses = await expenseDB.getAll();
      setExpenses(allExpenses);
    } catch (error) {
      console.error('Error loading expenses:', error);
      fallbackToLocalStorage();
    } finally {
      setIsLoading(false);
    }
  };

  // Save expenses to localStorage (fallback)
  useEffect(() => {
    if (!dbInitialized && expenses.length > 0) {
      localStorage.setItem('expenses', JSON.stringify(expenses));
    }
  }, [expenses, dbInitialized]);

  // Add new expense
  const addExpense = async (expense) => {
    const newExpense = {
      ...expense,
      id: Date.now(), // Simple way to generate unique ID
      date: new Date().toISOString().slice(0, 10) // YYYY-MM-DD format
    };

    if (dbInitialized) {
      try {
        await expenseDB.add(newExpense);
        // Reload expenses from DB
        loadExpenses();
      } catch (error) {
        console.error('Error adding expense:', error);
        // Fall back to state-only update
        setExpenses(prevExpenses => [...prevExpenses, newExpense]);
      }
    } else {
      // If DB not initialized, just update state
      setExpenses(prevExpenses => [...prevExpenses, newExpense]);
    }
  };

  // Update existing expense
  const updateExpense = async (updatedExpense) => {
    if (dbInitialized) {
      try {
        await expenseDB.update(updatedExpense);
        // Reload expenses from DB
        loadExpenses();
      } catch (error) {
        console.error('Error updating expense:', error);
        // Fall back to state-only update
        setExpenses(prevExpenses => 
          prevExpenses.map(expense => 
            expense.id === updatedExpense.id ? updatedExpense : expense
          )
        );
      }
    } else {
      // If DB not initialized, just update state
      setExpenses(prevExpenses => 
        prevExpenses.map(expense => 
          expense.id === updatedExpense.id ? updatedExpense : expense
        )
      );
    }
  };

  // Delete expense
  const deleteExpense = async (id) => {
    if (dbInitialized) {
      try {
        await expenseDB.delete(id);
        // Reload expenses from DB
        loadExpenses();
      } catch (error) {
        console.error('Error deleting expense:', error);
        // Fall back to state-only update
        setExpenses(prevExpenses => prevExpenses.filter(expense => expense.id !== id));
      }
    } else {
      // If DB not initialized, just update state
      setExpenses(prevExpenses => prevExpenses.filter(expense => expense.id !== id));
    }
  };

  // Calculate total expenses
  const totalExpenses = expenses.reduce((total, expense) => total + parseFloat(expense.amount), 0);

  // Render content based on active tab
  const renderContent = () => {
    if (isLoading) {
      return (
        <div className="flex justify-center items-center py-12">
          <div className="text-indigo-400">
            <svg className="animate-spin h-10 w-10 mr-3" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
          </div>
          <p className="text-indigo-300 text-lg font-medium">Loading...</p>
        </div>
      );
    }

    switch (activeTab) {
      case 'settings':
        return <Settings dbInitialized={dbInitialized} />;
      case 'home':
      default:
        return (
          <>
            <ExpenseForm addExpense={addExpense} dbInitialized={dbInitialized} />
            <ExpenseSummary expenses={expenses} total={totalExpenses} />
            <ExpenseChart expenses={expenses} />
            <ExpenseList 
              expenses={expenses} 
              deleteExpense={deleteExpense} 
              updateExpense={updateExpense} 
              dbInitialized={dbInitialized}
            />
          </>
        );
    }
  };

  return (
    <>
      {/* Sticky Header */}
      <header className="fixed top-0 left-0 right-0 bg-gray-900 shadow-md z-30">
        <div className="max-w-4xl mx-auto px-6 py-3 flex items-center justify-center md:justify-between">
          <div className="flex items-center">
            {/* Money icon from Boxicons CDN */}
            <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-indigo-400 mr-3" viewBox="0 0 24 24" fill="currentColor">
              <path d="M11.8 10.9c-2.27-.59-3-1.2-3-2.15 0-1.09 1.01-1.85 2.7-1.85 1.78 0 2.44.85 2.5 2.1h2.21c-.07-1.72-1.12-3.3-3.21-3.81V3h-3v2.16c-1.94.42-3.5 1.68-3.5 3.61 0 2.31 1.91 3.46 4.7 4.13 2.5.6 3 1.48 3 2.41 0 .69-.49 1.79-2.7 1.79-2.06 0-2.87-.92-2.98-2.1h-2.2c.12 2.19 1.76 3.42 3.68 3.83V21h3v-2.15c1.95-.37 3.5-1.5 3.5-3.55 0-2.84-2.43-3.81-4.7-4.4z"/>
            </svg>
            <h1 className="text-2xl font-bold text-indigo-400">Rupiah Expense Tracker</h1>
          </div>

          {/* Desktop tabs - move to header */}
          <div className="hidden md:block">
            <div className="flex">
              <button 
                onClick={() => setActiveTab('home')} 
                className={`px-4 py-1 mx-1 rounded-md ${activeTab === 'home' ? 'bg-indigo-600 text-white' : 'text-gray-300 hover:bg-gray-700'}`}
              >
                Home
              </button>
              <button 
                onClick={() => setActiveTab('settings')} 
                className={`px-4 py-1 mx-1 rounded-md ${activeTab === 'settings' ? 'bg-indigo-600 text-white' : 'text-gray-300 hover:bg-gray-700'}`}
              >
                Settings
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-4xl mx-auto px-6 pb-24 pt-20">
        {/* Subtitle and storage indicator outside of sticky header */}
        <div className="text-center mb-8 mt-2">
          <p className="text-gray-400">Keep track of your expenses in Indonesian Rupiah</p>
          {dbInitialized && (
            <div className="mt-2 inline-flex items-center text-sm text-green-400">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
              Using IndexedDB
            </div>
          )}
        </div>

        {renderContent()}

        {/* Show bottom navigation on mobile */}
        <div className="md:hidden">
          <BottomNavigation activeTab={activeTab} setActiveTab={setActiveTab} />
        </div>
      </div>
    </>
  );
}

export default App;
