import React, { useState, useEffect } from 'react';
import { categoryDB } from '../utils/db';

function Settings({ dbInitialized = false }) {
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState({ id: '', name: '' });
  const [editingCategory, setEditingCategory] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  // Load categories
  useEffect(() => {
    const loadCategories = async () => {
      try {
        if (dbInitialized) {
          // Load from IndexedDB
          const data = await categoryDB.getAll();
          setCategories(data);
        } else {
          // Fallback to localStorage
          loadFromLocalStorage();
        }
      } catch (error) {
        console.error('Error loading categories:', error);
        loadFromLocalStorage();
      } finally {
        setIsLoading(false);
      }
    };

    loadCategories();
  }, [dbInitialized]);

  // Load from localStorage if needed
  const loadFromLocalStorage = () => {
    const savedCategories = localStorage.getItem('expense-categories');
    if (savedCategories) {
      setCategories(JSON.parse(savedCategories));
    } else {
      // Default categories tailored for Indonesian lifestyle and expenses
      const defaultCategories = [
        // Daily Necessities
        { id: 'makanan', name: 'Makanan Sehari-hari' },
        { id: 'jajanan', name: 'Jajanan & Camilan' },
        { id: 'makan_luar', name: 'Makan di Luar' },
        { id: 'kopi', name: 'Kopi & Minuman' },

        // Transportation
        { id: 'bensin', name: 'Bensin' },
        { id: 'gojek', name: 'Ojek Online & Taksi' },
        { id: 'transportasi_umum', name: 'Transportasi Umum' },
        { id: 'parkir', name: 'Parkir & Tol' },
        { id: 'perawatan_kendaraan', name: 'Perawatan Kendaraan' },

        // Housing & Utilities
        { id: 'sewa_rumah', name: 'Sewa Rumah/Kos' },
        { id: 'listrik', name: 'Listrik' },
        { id: 'air', name: 'Air PDAM' },
        { id: 'internet', name: 'Internet & WiFi' },
        { id: 'pulsa', name: 'Pulsa & Paket Data' },

        // Personal Care
        { id: 'perawatan_diri', name: 'Perawatan Diri' },
        { id: 'kesehatan', name: 'Kesehatan & Obat' },
        { id: 'pakaian', name: 'Pakaian & Sepatu' },
        { id: 'salon', name: 'Salon & Perawatan' },

        // Entertainment & Leisure
        { id: 'hiburan', name: 'Hiburan & Rekreasi' },
        { id: 'liburan', name: 'Liburan & Wisata' },
        { id: 'olahraga', name: 'Olahraga & Hobi' },
        { id: 'streaming', name: 'Langganan Digital' },

        // Education
        { id: 'pendidikan', name: 'Pendidikan & Kursus' },
        { id: 'buku', name: 'Buku & Alat Tulis' },

        // Social & Religious
        { id: 'sedekah', name: 'Sedekah & Donasi' },
        { id: 'zakat', name: 'Zakat' },
        { id: 'arisan', name: 'Arisan' },
        { id: 'hadiah', name: 'Hadiah & Sumbangan' },

        // Family
        { id: 'anak', name: 'Kebutuhan Anak' },
        { id: 'keluarga', name: 'Kiriman Keluarga' },

        // Investments & Savings
        { id: 'tabungan', name: 'Tabungan' },
        { id: 'investasi', name: 'Investasi' },
        { id: 'asuransi', name: 'Asuransi' },

        // Special Occasions
        { id: 'lebaran', name: 'Lebaran & Hari Raya' },
        { id: 'acara', name: 'Acara & Perayaan' },

        // Online Shopping
        { id: 'belanja_online', name: 'Belanja Online' },
        { id: 'elektronik', name: 'Gadget & Elektronik' },

        // Miscellaneous
        { id: 'lainnya', name: 'Pengeluaran Lainnya' }
      ];
      setCategories(defaultCategories);
      localStorage.setItem('expense-categories', JSON.stringify(defaultCategories));
    }
  };

  // Save categories to localStorage (if not using IndexedDB)
  useEffect(() => {
    if (!dbInitialized && categories.length > 0) {
      localStorage.setItem('expense-categories', JSON.stringify(categories));
    }
  }, [categories, dbInitialized]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'id') {
      // Ensure ID is lowercase, no spaces, and only alphanumeric characters
      const formattedValue = value.toLowerCase().replace(/[^a-z0-9]/g, '');
      setNewCategory({ ...newCategory, [name]: formattedValue });
    } else {
      setNewCategory({ ...newCategory, [name]: value });
    }
  };

  // Add new category
  const handleAddCategory = async (e) => {
    e.preventDefault();

    // Validation
    if (!newCategory.id.trim() || !newCategory.name.trim()) {
      setError('Both ID and Name are required');
      return;
    }

    // Check for duplicate ID
    if (categories.some(cat => cat.id === newCategory.id)) {
      setError('A category with this ID already exists');
      return;
    }

    try {
      if (dbInitialized) {
        await categoryDB.add(newCategory);
        const updatedCategories = await categoryDB.getAll();
        setCategories(updatedCategories);
      } else {
        setCategories([...categories, newCategory]);
      }
      setNewCategory({ id: '', name: '' });
      setError('');
    } catch (error) {
      console.error('Error adding category:', error);
      setError('Failed to add category. Please try again.');
    }
  };

  // Start editing a category
  const handleEditStart = (category) => {
    setEditingCategory(category);
    setNewCategory({ id: category.id, name: category.name });
  };

  // Save edited category
  const handleSaveEdit = async () => {
    if (!newCategory.id.trim() || !newCategory.name.trim()) {
      setError('Both ID and Name are required');
      return;
    }

    // Check for duplicate ID (excluding the current category being edited)
    if (categories.some(cat => cat.id === newCategory.id && cat.id !== editingCategory.id)) {
      setError('A category with this ID already exists');
      return;
    }

    try {
      if (dbInitialized) {
        await categoryDB.update(newCategory);
        const updatedCategories = await categoryDB.getAll();
        setCategories(updatedCategories);
      } else {
        setCategories(
          categories.map(cat => 
            cat.id === editingCategory.id ? newCategory : cat
          )
        );
      }

      setEditingCategory(null);
      setNewCategory({ id: '', name: '' });
      setError('');
    } catch (error) {
      console.error('Error updating category:', error);
      setError('Failed to update category. Please try again.');
    }
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingCategory(null);
    setNewCategory({ id: '', name: '' });
    setError('');
  };

  // Delete category
  const handleDeleteCategory = async (id) => {
    // Ask for confirmation
    if (window.confirm('Are you sure you want to delete this category? This may affect existing expenses.')) {
      try {
        if (dbInitialized) {
          await categoryDB.delete(id);
          const updatedCategories = await categoryDB.getAll();
          setCategories(updatedCategories);
        } else {
          setCategories(categories.filter(cat => cat.id !== id));
        }
      } catch (error) {
        console.error('Error deleting category:', error);
        alert('Failed to delete category. Please try again.');
      }
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center py-12">
        <div className="text-indigo-400">
          <svg className="animate-spin h-8 w-8 mr-3" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
        </div>
        <p className="text-indigo-300">Loading categories...</p>
      </div>
    );
  }

  return (
    <div className="pb-20 mb-16">
      <h2 className="text-xl font-semibold mb-6 text-indigo-300">Manage Categories</h2>

      {/* Storage indicator */}
      <div className="mb-4 text-sm">
        <span className="inline-flex items-center text-gray-400">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v2H7a1 1 0 100 2h2v2a1 1 0 102 0v-2h2a1 1 0 100-2h-2V7z" clipRule="evenodd" />
          </svg>
          Storage: {dbInitialized ? 'IndexedDB' : 'LocalStorage'}
        </span>
      </div>

      {/* Form for adding/editing categories */}
      <div className="bg-gray-800 p-6 rounded-lg shadow-lg mb-6">
        <h3 className="text-lg font-medium text-white mb-4">
          {editingCategory ? 'Edit Category' : 'Add New Category'}
        </h3>

        {error && (
          <div className="bg-red-900 text-white p-3 rounded mb-4">
            {error}
          </div>
        )}

        <form onSubmit={e => e.preventDefault()}>
          <div className="mb-4">
            <label htmlFor="id" className="block mb-2 font-medium text-gray-300">
              ID (lowercase, no spaces)
            </label>
            <input
              type="text"
              id="id"
              name="id"
              value={newCategory.id}
              onChange={handleInputChange}
              className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="e.g., groceries"
              disabled={editingCategory !== null}
            />
          </div>

          <div className="mb-4">
            <label htmlFor="name" className="block mb-2 font-medium text-gray-300">
              Display Name
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={newCategory.name}
              onChange={handleInputChange}
              className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="e.g., Groceries"
            />
          </div>

          <div className="flex gap-2">
            {editingCategory ? (
              <>
                <button
                  onClick={handleSaveEdit}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                >
                  Save Changes
                </button>
                <button
                  onClick={handleCancelEdit}
                  className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-300"
                >
                  Cancel
                </button>
              </>
            ) : (
              <button
                onClick={handleAddCategory}
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
              >
                Add Category
              </button>
            )}
          </div>
        </form>
      </div>

      {/* List of categories */}
      <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden">
        <h3 className="text-lg font-medium text-white p-4 border-b border-gray-700">
          Categories
        </h3>

        {categories.length === 0 ? (
          <div className="p-4 text-gray-400">No categories defined.</div>
        ) : (
          <ul>
            {categories.map(category => (
              <li key={category.id} className="border-b border-gray-700 last:border-b-0">
                <div className="flex justify-between items-center p-4">
                  <div>
                    <div className="text-white font-medium">{category.name}</div>
                    <div className="text-gray-400 text-sm">ID: {category.id}</div>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleEditStart(category)}
                      className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors duration-300"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteCategory(category.id)}
                      className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition-colors duration-300"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Settings;
