import React, { useState, forwardRef, useImperativeHandle } from 'react';
import Badge from './Badge';

const TagSelector = forwardRef(({ selectedTags = [], availableTags = [], onChange }, ref) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isAddTagModalOpen, setIsAddTagModalOpen] = useState(false);
  const [newTagName, setNewTagName] = useState('');
  const [temporaryTags, setTemporaryTags] = useState([]);

  // Expose functions to parent component
  useImperativeHandle(ref, () => ({
    openAddTagModal: () => setIsAddTagModalOpen(true)
  }));

  // Filter tags based on search term
  const filteredTags = [...availableTags, ...temporaryTags]
    .filter(tag => tag.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .filter(tag => !selectedTags.includes(tag.id));

  // Handle tag selection
  const handleTagSelect = (tagId) => {
    const newSelectedTags = [...selectedTags, tagId];
    onChange(newSelectedTags);
    setSearchTerm('');
  };

  // Handle tag removal
  const handleTagRemove = (tagId) => {
    const newSelectedTags = selectedTags.filter(id => id !== tagId);
    onChange(newSelectedTags);
  };

  // Get tag object by id
  const getTagById = (tagId) => {
    return [...availableTags, ...temporaryTags].find(tag => tag.id === tagId);
  };

  // Color assignments for tags - distribute evenly across available colors
  const tagColors = [
    'blue', 'green', 'yellow', 'indigo', 'purple', 
    'pink', 'lime', 'rose', 'teal', 'cyan', 'orange'
  ];

  const getTagColor = (index) => {
    return tagColors[index % tagColors.length];
  };

  // Handle adding a new tag
  const handleAddNewTag = () => {
    if (!newTagName.trim()) return;

    // Generate a temporary ID that won't conflict with existing tags
    const tempId = `temp_${Date.now()}`;

    // Add to temporary tags
    const newTag = { id: tempId, name: newTagName.trim() };
    setTemporaryTags([...temporaryTags, newTag]);

    // Add to selected tags
    handleTagSelect(tempId);

    // Reset form
    setNewTagName('');
    setIsAddTagModalOpen(false);
  };

  // Handle Enter key press in the new tag input
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleAddNewTag();
    }
  };

  return (
    <div className="space-y-4">
      {/* Selected Tags Section */}
      {selectedTags.length > 0 && (
        <div className="p-4 flex flex-wrap gap-2 mb-4 border-b border-gray-700">
          {selectedTags.map((tagId, index) => {
            const tag = getTagById(tagId);
            return tag ? (
              <Badge 
                key={tagId} 
                color={getTagColor([...availableTags, ...temporaryTags].findIndex(t => t.id === tagId))} 
                removable={true}
                onRemove={() => handleTagRemove(tagId)}
              >
                {tag.name}
              </Badge>
            ) : null;
          })}
        </div>
      )}

      {/* Search Box */}
      <div className="relative mb-4">
        <input
          type="text"
          className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
          placeholder="Search tags..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Available Tags */}
      <div className="flex flex-wrap gap-2">
        {filteredTags.length === 0 ? (
          <p className="text-gray-400 text-sm">No matching tags found</p>
        ) : (
          filteredTags.map((tag, index) => (
            <Badge 
              key={tag.id}
              color={getTagColor([...availableTags, ...temporaryTags].findIndex(t => t.id === tag.id))}
              className="cursor-pointer hover:opacity-80 transition-opacity"
              onClick={() => handleTagSelect(tag.id)}
            >
              {tag.name}
            </Badge>
          ))
        )}
      </div>

      {/* Add New Tag Modal */}
      {isAddTagModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center">
          <div className="bg-gray-800 rounded-lg shadow-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-medium text-white mb-4">Add New Tag</h3>
            <div className="mb-4">
              <input
                type="text"
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="Tag name"
                value={newTagName}
                onChange={(e) => setNewTagName(e.target.value)}
                onKeyDown={handleKeyDown}
                autoFocus
              />
            </div>
            <div className="flex justify-end gap-2">
              <button
                type="button"
                className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-300"
                onClick={() => {
                  setNewTagName('');
                  setIsAddTagModalOpen(false);
                }}
              >
                Cancel
              </button>
              <button
                type="button"
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
                onClick={handleAddNewTag}
                disabled={!newTagName.trim()}
              >
                Add Tag
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
});

export function TagSelectorWithLabel({ selectedTags, availableTags, onChange, id = "tags" }) {
  const tagSelectorRef = React.useRef();

  return (
    <div className="mb-4">
      <div className="flex justify-between items-center mb-2">
        <label htmlFor={id} className="font-medium text-gray-300">Tags</label>
        <button
          type="button"
          className="text-xs px-2 py-1 bg-indigo-600 hover:bg-indigo-700 text-white rounded-md transition-colors"
          onClick={() => tagSelectorRef.current?.openAddTagModal()}
        >
          + Add New Tag
        </button>
      </div>
      <TagSelector 
        ref={tagSelectorRef}
        selectedTags={selectedTags}
        availableTags={availableTags}
        onChange={onChange}
      />
    </div>
  );
}

export default TagSelector;
