import React, { useState, useEffect } from 'react';
import ExpenseForm from './components/ExpenseForm';
import ExpenseList from './components/ExpenseList';
import ExpenseSummary from './components/ExpenseSummary';
import ExpenseChart from './components/ExpenseChart';
import Settings from './components/Settings';
import BottomNavigation from './components/BottomNavigation';

function App() {
  const [activeTab, setActiveTab] = useState('home');
  const [expenses, setExpenses] = useState(() => {
    const savedExpenses = localStorage.getItem('expenses');
    return savedExpenses ? JSON.parse(savedExpenses) : [];
  });

  // Save expenses to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('expenses', JSON.stringify(expenses));
  }, [expenses]);

  // Add new expense
  const addExpense = (expense) => {
    const newExpense = {
      ...expense,
      id: Date.now(), // Simple way to generate unique ID
      date: new Date().toISOString().slice(0, 10) // YYYY-MM-DD format
    };
    setExpenses([...expenses, newExpense]);
  };

  // Delete expense
  const deleteExpense = (id) => {
    setExpenses(expenses.filter(expense => expense.id !== id));
  };

  // Calculate total expenses
  const totalExpenses = expenses.reduce((total, expense) => total + parseFloat(expense.amount), 0);

  // Render content based on active tab
  const renderContent = () => {
    switch (activeTab) {
      case 'settings':
        return <Settings />;
      case 'home':
      default:
        return (
          <>
            <ExpenseForm addExpense={addExpense} />
            <ExpenseSummary expenses={expenses} total={totalExpenses} />
            <ExpenseChart expenses={expenses} />
            <ExpenseList expenses={expenses} deleteExpense={deleteExpense} />
          </>
        );
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 pb-24">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-indigo-400 mb-2">Rupiah Expense Tracker</h1>
        <p className="text-gray-400">Keep track of your expenses in Indonesian Rupiah</p>
      </div>

      {renderContent()}

      {/* Show bottom navigation on mobile */}
      <div className="md:hidden">
        <BottomNavigation activeTab={activeTab} setActiveTab={setActiveTab} />
      </div>

      {/* Show tab navigation on desktop */}
      <div className="hidden md:block fixed top-6 right-6">
        <div className="bg-gray-800 rounded-lg overflow-hidden">
          <button 
            onClick={() => setActiveTab('home')} 
            className={`px-4 py-2 ${activeTab === 'home' ? 'bg-indigo-600 text-white' : 'text-gray-300 hover:bg-gray-700'}`}
          >
            Home
          </button>
          <button 
            onClick={() => setActiveTab('settings')} 
            className={`px-4 py-2 ${activeTab === 'settings' ? 'bg-indigo-600 text-white' : 'text-gray-300 hover:bg-gray-700'}`}
          >
            Settings
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
